package com.vaadin.demo.component.map;

import com.vaadin.flow.component.map.configuration.style.Icon;

public class Icons {
    // DS publisher can not resolve default marker image, nor is there a way to
    // host static images with a deterministic URL
    // For now duplicate all required icons with base64 encoded URL
    public static final Icon DEFAULT_MARKER_ICON;

    public static final Icon GERMAN_FLAG_ICON;

    public static final Icon US_FLAG_ICON;

    static {
        Icon.Options defaultMarkerIconOptions = new Icon.Options();
        defaultMarkerIconOptions.setSrc(
                " data:image/png;base64,");
        defaultMarkerIconOptions.setImgSize(new Icon.ImageSize(80, 104));
        defaultMarkerIconOptions.setScale(0.5f);
        defaultMarkerIconOptions.setAnchorOrigin(Icon.AnchorOrigin.BOTTOM_LEFT);
        defaultMarkerIconOptions.setAnchor(new Icon.Anchor(0.5f, 0.12f));
        DEFAULT_MARKER_ICON = new Icon(defaultMarkerIconOptions);

        Icon.Options germanFlagIconOptions = new Icon.Options();
        germanFlagIconOptions.setSrc(
                "data:image/png;base64,");
        germanFlagIconOptions.setImgSize(new Icon.ImageSize(48, 48));
        germanFlagIconOptions.setScale(0.6f);
        GERMAN_FLAG_ICON = new Icon(germanFlagIconOptions);

        Icon.Options usFlagIconOptions = new Icon.Options();
        usFlagIconOptions.setSrc(
                "data:image/png;base64,");
        usFlagIconOptions.setImgSize(new Icon.ImageSize(48, 48));
        usFlagIconOptions.setScale(0.6f);
        US_FLAG_ICON = new Icon(usFlagIconOptions);
    }
}
