package com.vaadin.demo.component.map;

import com.vaadin.flow.component.map.Assets;
import com.vaadin.flow.component.map.configuration.style.Icon;

public class Icons {
    // DS publisher can not resolve default marker image, nor is there a way to host static images with a deterministic URL
    // For now duplicate all required icons with base64 encoded URL
    public static Icon defaultMarkerIcon = new Icon(new Icon.Options()
            .setSrc("data:image/png;base64,")
            .setImgSize(new Icon.ImageSize(Assets.DEFAULT_MARKER.getWidth(), Assets.DEFAULT_MARKER.getHeight()))
            .setScale(0.2f)
            .setAnchorOrigin(Icon.AnchorOrigin.BOTTOM_LEFT)
            .setAnchor(new Icon.Anchor(0.5f, 0)));

    public static Icon germanFlagIcon = new Icon(new Icon.Options()
            .setSrc("data:image/png;base64,")
            .setImgSize(new Icon.ImageSize(48, 48))
            .setScale(0.6f));

    public static Icon usFlagIcon = new Icon(new Icon.Options()
            .setSrc("data:image/png;base64,")
            .setImgSize(new Icon.ImageSize(48, 48))
            .setScale(0.6f));
}
